import Global from "./styles/global.js";
import styled from "styled-components";
import Form from "./components/Form.js";
import Grid from "./components/Grid.js";
import { useEffect, useState } from "react";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import axios from "axios";

const Container = styled.div`
    width: 100%;
    max-width: 800px;
    margin-top: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
`;

const Title = styled.h2``;

function App() {
  const [users, setUsers] = useState([]);
  const [onEdit, setOnedit] = useState(null);

  const getUsers = async () => {
    try {
      const res = await axios.get("http://localhost:8800");
      setUsers(res.data.sort((a, b) => (a.nome > b.nome ? 1 : -1)));
    } catch (error) {
      toast.error(error);
    }
  };

  useEffect(() => {
    getUsers();
  }, [setUsers]);

  return (
    <>
      <Container>
        <Title>USU√ÅRIOS</Title>
        <Form onEdit={onEdit} setUsers={setUsers} setOnEdit={setOnedit} getUsers={getUsers} />
        <Grid users={users} setUsers={setUsers} setOnEdit={setOnedit} position={toast.position} closeOnClick={true}/>
      </Container>
      <ToastContainer autoClose={3000} position={toast.position} closeOnClick={true} />
      <Global/> 
    </>
  );
}

export default App;
